import React, { useState, useEffect } from 'react';
import PetList from '../searchindex/cards.js';

export default function Favorite() {
    // react Hook For State Handler
    const [data, setData] = useState([]);

    // Fetch Function
    useEffect(()=>{
        const data = JSON.parse(localStorage.getItem('data'));
        // console.log(typeof(data));
		let tempArr = [];
        Object.entries(data).forEach(([key, dog]) => {
            // console.log(dog)
            if (('like' in dog) && (dog['like'] === 1)) {
                tempArr.push(dog);
            }
        });
        setData(tempArr);
	}, []);

    return (
        <div>
            {data.length > 0 && (
                <PetList pets={data} />
            )}
            {data.length === 0 && (<div className={'empty'}>
          <h3>No Paws in your favorite list now</h3>
          <h3>Find your lovely paws Now!</h3>
          <img src="/img/dog_empty.gif" width={400}/>
        </div>)
            }
        </div>
    );
}
