import { Link } from "react-router-dom";
import { getAuth, signOut } from 'firebase/auth';
import {useAuth} from "../../auth/Auth";

const DEFAULT_USERS = [
    { userId: null, userName: "Log Out", userImg: '/img/null.png' }, //null user
    { userId: "penguin", userName: "Penguin", userImg: '/img/Penguin.png' },
    { userId: "parrot", userName: "Parrot", userImg: '/img/Parrot.png' },
    { userId: "zebra", userName: "Zebra", userImg: '/img/Zebra.png' },
  ]

export default function NavigationPC(props) {
    const currentUser = props.currentUser;
    const setCurrentUser = props.setCurrentUser;
    const {user, logout} = useAuth();

    const handleSignOut = (event) => {
      console.log("signing out");
      signOut(getAuth())
        .then(() => {
          logout();
          window.location.href = "/signin";
        })
    }

    const navItemArray = props.navList.map((item, index) => {
        return (
            <li key={index}>
                <Link to={item.link} aria-label={item.ariaLabel}>
                    {item.name}
                </Link>
            </li>
        )
    })

    return (
        <div className="nav-container">
            <div className="brand-container">
                <ul>
                    <li className="brand">
                        <a href="home_page.html" aria-label="Home">
                            <img src="/img/iron.png" alt=""
                                style={{
                                    height: '24px',
                                    marginRight: '5px',
                                }} />
                            Furever Home
                        </a>
                    </li>
                    <li>
                        <ul id='nav-pages'>
                            {navItemArray}
                        </ul>
                    </li>
                </ul>
            </div>
            <div className="login-container">
                <div id='login-block'>
                    <ul className="login">
                        {user &&
                            <>
                            <li>
                            <button className={'btn'} aria-label="Sign Out" onClick={handleSignOut}>Sign Out</button>
                            </li>
                            </>
                        }
                        {!user  &&
                            <li><a href="signin" aria-label="Login">Sign in</a></li>
                        }
                    </ul>
                </div>
            </div>
        </div>
    );
}
