import {Link, useNavigate} from "react-router-dom";
import React, { useState } from 'react';
import {useAuth} from "../../auth/Auth";

// click favorite icon to favorite list page, click other parts inside card to detail page
function PetCard({ pet }) {
    const {user} = useAuth();
    const navigate = useNavigate();
    const [like, setLike] = useState('like' in pet ? pet['like'] : 0);  // 0 for non-like, 1 for like
    // console.log(like);

    // useEffect(() => {
    //     if ('like' in pet) {  // if pet object has 'like' value, then update it
    //         setLike(pet.like);
    //     }
    // }, [pet]);

    const id = pet.id;
    const key = 'dog' + id;

    const handleLike = (e) => {
        if (!user) {
            return navigate('/signin');
        }
        if (localStorage.getItem('data') === null) {  // first time to raed local json database
            fetch('/database.json')
                .then(res => res.json())
                .then(data => {
                    const likeStatus = 'like' in data[key] ? data[key]['like'] : 0;
                    data[key]['like'] = (likeStatus === 0 ? 1 : 0);
                    localStorage.setItem('data', JSON.stringify(data));
            })
        } else {  // if have updated local storage data, then read localstorage instead of json file
            const data = JSON.parse(localStorage.getItem('data'));
            const likeStatus = 'like' in data[key] ? data[key]['like'] : 0;
            data[key]['like'] = (likeStatus === 0 ? 1 : 0);
            localStorage.setItem('data', JSON.stringify(data));
        }

        setLike(like === 0 ? 1 : 0);  // toggle like state
    };

    return (
        <div className="col mb-4">
            <div className="card h-100">
                <span className="favorite-icon" onClick={handleLike}>
                    {like ? (
                        <i className="fa fa-heart" aria-hidden="true" style={{ color: "red" }}></i>
                    ) : (
                        <i className="fa fa-heart" aria-hidden="true"></i>
                    )}
                </span>
                <Link to={`/detail/${pet.id}`} aria-label="Go to dog detail page">
                    <img src={pet.images[0]} className="card-img-top" alt={pet.name} />
                    <div className="card-body">
                        <h5 className="card-title">{pet.name}</h5>
                        <p className="card-text">Breed: {pet.breed}</p>
                        <p className="card-text">Age: {pet.age}</p>
                        <p className="card-text">Gender: {pet.gender}</p>
                    </div>
                </Link>
            </div>
        </div>
    );
};

function PetList({ pets }) {
    // map the data into <PetCard> elements
    const petsListWithLike = pets.map(pet => {
        const data = JSON.parse(localStorage.getItem('data'));
        if (data) {
            const petListsLocal = Object.values(data);
            const petItem = petListsLocal.find(item => item.id === pet.id);
            let liked = 0;
            if (petItem && petItem.like === 1) {
                liked = 1;
            }
            pet.like = liked;
        }

        return pet;
    })
    const petCardArray = petsListWithLike.map((pet) => {


        return (
            <div key={pet.id}>
                {/* <Link to={`/detail/${pet.id}`} aria-label="Go to dog detail page">
                    <PetCard pet={pet} />
                </Link> */}
                <PetCard pet={pet} />
            </div>
        )
    })

    return (
        <section className="bg-color">
            <div className="container-full">
                <div className="row row-cols-1 row-cols-sm-2 row-cols-md-3 row-cols-lg-4">
                    {petCardArray}
                </div>
            </div>
        </section>
    );
};

export default PetList;
